import{_ as e,c as i,o as l,a3 as r}from"./chunks/framework.BXNB9gP7.js";const h=JSON.parse('{"title":"GATOS — STAKEHOLDERS & GOALS","description":"","frontmatter":{},"headers":[],"relativePath":"STAKEHOLDERS.md","filePath":"STAKEHOLDERS.md","lastUpdated":1763125191000}'),o={name:"STAKEHOLDERS.md"};function t(s,a,n,d,p,c){return l(),i("div",{"data-pagefind-body":!0},[...a[0]||(a[0]=[r('<h1 id="gatos-—-stakeholders-goals" tabindex="-1">GATOS — STAKEHOLDERS &amp; GOALS <a class="header-anchor" href="#gatos-—-stakeholders-goals" aria-label="Permalink to &quot;GATOS — STAKEHOLDERS &amp; GOALS&quot;">​</a></h1><p><a id="gatos--stakeholders--goals"></a></p><p><a id="gatos--stakeholders--goals"></a><a id="gatos-stakeholders-goals"></a></p><ul><li><a href="#stakeholders">Stakeholders</a><ul><li><a href="#app-developer-dev">App Developer (DEV)</a></li><li><a href="#platform-engineer-peng">Platform Engineer (PENG)</a></li><li><a href="#securitycompliance-sec">Security/Compliance (SEC)</a></li><li><a href="#sre--ops-sre">SRE / Ops (SRE)</a></li><li><a href="#dataml-engineer-dml">Data/ML Engineer (DML)</a></li><li><a href="#auditor--grc-aud">Auditor / GRC (AUD)</a></li><li><a href="#llmagent-agent">LLM/Agent (AGENT)</a></li><li><a href="#productprogram-manager-pm">Product/Program Manager (PM)</a></li></ul></li><li><a href="#goals-synopsis">Goals Synopsis</a></li></ul><blockquote><p>[!NOTE] We define primary stakeholders and their core goals. (User stories are generated per feature in <code>FEATURES.md</code>.)</p></blockquote><h2 id="stakeholders" tabindex="-1">Stakeholders <a class="header-anchor" href="#stakeholders" aria-label="Permalink to &quot;Stakeholders&quot;">​</a></h2><p><a id="stakeholders"></a></p><p><a id="stakeholders"></a></p><h3 id="app-developer-dev" tabindex="-1">App Developer (DEV) <a class="header-anchor" href="#app-developer-dev" aria-label="Permalink to &quot;App Developer (DEV)&quot;">​</a></h3><p><a id="app-developer-dev"></a></p><p><a id="app-developer-dev"></a> — build features,</p><ul><li>test locally,</li><li>commit artifacts,</li><li>script automations.</li></ul><h3 id="platform-engineer-peng" tabindex="-1">Platform Engineer (PENG) <a class="header-anchor" href="#platform-engineer-peng" aria-label="Permalink to &quot;Platform Engineer (PENG)&quot;">​</a></h3><p><a id="platform-engineer-peng"></a></p><p><a id="platform-engineer-peng"></a></p><ul><li>operate repos at scale,</li><li>enforce policy,</li><li>ensure latency/throughput,</li><li>maintain health.</li></ul><h3 id="security-compliance-sec" tabindex="-1">Security/Compliance (SEC) <a class="header-anchor" href="#security-compliance-sec" aria-label="Permalink to &quot;Security/Compliance (SEC)&quot;">​</a></h3><p><a id="securitycompliance-sec"></a></p><p><a id="securitycompliance-sec"></a> — enforce least privilege,</p><ul><li>attest changes,</li><li>audit trails,</li><li>key rotation.</li></ul><h3 id="sre-ops-sre" tabindex="-1">SRE / Ops (SRE) <a class="header-anchor" href="#sre-ops-sre" aria-label="Permalink to &quot;SRE / Ops (SRE)&quot;">​</a></h3><p><a id="sre--ops-sre"></a></p><p><a id="sre--ops-sre"></a><a id="sre-ops-sre"></a> — deploy safely,</p><ul><li>debug incidents,</li><li>maintain SLAs,</li><li>post-mortems.</li></ul><h3 id="data-ml-engineer-dml" tabindex="-1">Data/ML Engineer (DML) <a class="header-anchor" href="#data-ml-engineer-dml" aria-label="Permalink to &quot;Data/ML Engineer (DML)&quot;">​</a></h3><p><a id="dataml-engineer-dml"></a></p><p><a id="dataml-engineer-dml"></a> — manage large artifacts,</p><ul><li>track lineage,</li><li>reproduce results across air-gapped nodes.</li></ul><h3 id="auditor-grc-aud" tabindex="-1">Auditor / GRC (AUD) <a class="header-anchor" href="#auditor-grc-aud" aria-label="Permalink to &quot;Auditor / GRC (AUD)&quot;">​</a></h3><p><a id="auditor--grc-aud"></a></p><p><a id="auditor--grc-aud"></a><a id="auditor-grc-aud"></a> — independently verify history and policy decisions;</p><ul><li>ensure evidence sufficiency.</li></ul><h3 id="llm-agent-agent" tabindex="-1">LLM/Agent (AGENT) <a class="header-anchor" href="#llm-agent-agent" aria-label="Permalink to &quot;LLM/Agent (AGENT)&quot;">​</a></h3><p><a id="llmagent-agent"></a></p><p><a id="llmagent-agent"></a> — interact programmatically via JSONL;</p><ul><li>publish/consume messages under caps/policy.</li></ul><h3 id="product-program-manager-pm" tabindex="-1">Product/Program Manager (PM) <a class="header-anchor" href="#product-program-manager-pm" aria-label="Permalink to &quot;Product/Program Manager (PM)&quot;">​</a></h3><p><a id="productprogram-manager-pm"></a></p><p><a id="productprogram-manager-pm"></a> — visibility across environments,</p><ul><li>approvals,</li><li>governance gates.</li></ul><h2 id="goals-synopsis" tabindex="-1">Goals Synopsis <a class="header-anchor" href="#goals-synopsis" aria-label="Permalink to &quot;Goals Synopsis&quot;">​</a></h2><p><a id="goals-synopsis"></a></p><p><a id="goals-synopsis"></a></p><ul><li><code>DEV</code>: determinism, fast local loop, simple APIs.</li><li><code>PENG</code>: predictable ops (GC, epochs), observability, policy profiles.</li><li><code>SEC</code>: artifacts-as-law, explainable denies, signatures everywhere.</li><li><code>SRE</code>: exactly-once buses, replayable state, clean audit of deploys.</li><li><code>DML</code>: chunking/opaque pointers, RLS export, provenance.</li><li><code>AUD</code>: portable verification, no SaaS dependency, proof envelopes.</li><li><code>AGENT</code>: streaming JSONL, idempotent semantics, backpressure.</li><li><code>PM</code>: approvals/quorum, evidence dashboards, governance diffs.</li></ul>',44)])])}const m=e(o,[["render",t]]);export{h as __pageData,m as default};
