import{_ as a,c as e,o as r,a3 as d}from"./chunks/framework.BXNB9gP7.js";const c=JSON.parse('{"title":"GATOS — USE CASES","description":"","frontmatter":{},"headers":[],"relativePath":"USE-CASES.md","filePath":"USE-CASES.md","lastUpdated":1763125249000}'),o={name:"USE-CASES.md"};function s(i,t,n,l,p,h){return r(),e("div",{"data-pagefind-body":!0},[...t[0]||(t[0]=[d('<h1 id="gatos-—-use-cases" tabindex="-1">GATOS — USE CASES <a class="header-anchor" href="#gatos-—-use-cases" aria-label="Permalink to &quot;GATOS — USE CASES&quot;">​</a></h1><p><a id="gatos--use-cases"></a></p><p><a id="gatos--use-cases"></a><a id="gatos-use-cases"></a></p><ul><li><a href="#1">1) Programmable Git (Policy-Enforced Repos)</a></li><li><a href="#2">2) Distributed State Machines (Deterministic)</a></li><li><a href="#3">3) Distributed General Computer (Agents on a Bus)</a></li><li><a href="#4">4) Supply-Chain &amp; Deploy Attestation</a></li><li><a href="#5">5) Air-Gapped ML Registry</a></li><li><a href="#6">6) Cross-App Data Sharing (RLS-gated)</a></li><li><a href="#7">7) Knowledge Graph for Code &amp; Ops</a></li><li><a href="#8">8) Regulated Feature Flags &amp; Config</a></li></ul><p><a id="gatos-use-cases"></a></p><blockquote><p><em>Have <strong>you</strong> tried GATOS yet?</em></p></blockquote><p>This document illustrates practical scenarios where GATOS provides unique value.</p><hr><h2 id="_1-programmable-git-policy-enforced-repos" tabindex="-1">1) Programmable Git (Policy-Enforced Repos) <a class="header-anchor" href="#_1-programmable-git-policy-enforced-repos" aria-label="Permalink to &quot;1) Programmable Git (Policy-Enforced Repos)&quot;">​</a></h2><p><a id="1"></a><a id="1-programmable-git-policy-enforced-repos"></a></p><p><a id="1"></a><a id="1-programmable-git-policy-enforced-repos"></a></p><table tabindex="0"><thead><tr><th></th><th></th></tr></thead><tbody><tr><td><strong>Goal</strong></td><td>Treat Git as a programmable ledger with rule-checked writes.</td></tr><tr><td><strong>How</strong></td><td>Journals under <code>refs/gatos/journal/**</code>, policy gate enforces who/what/where, audits on deny.</td></tr><tr><td><strong>Why GATOS</strong></td><td>No server required (local), or push-gate profile for RYW and centralized enforcement.</td></tr></tbody></table><hr><h2 id="_2-distributed-state-machines-deterministic" tabindex="-1">2) Distributed State Machines (Deterministic) <a class="header-anchor" href="#_2-distributed-state-machines-deterministic" aria-label="Permalink to &quot;2) Distributed State Machines (Deterministic)&quot;">​</a></h2><p><a id="2"></a><a id="2-distributed-state-machines-deterministic"></a></p><p><a id="2"></a><a id="2-distributed-state-machines-deterministic"></a></p><table tabindex="0"><thead><tr><th></th><th></th></tr></thead><tbody><tr><td><strong>Goal</strong></td><td>Model business processes as append-only events → deterministic state.</td></tr><tr><td><strong>How</strong></td><td>Echo folds compute <code>state_root</code>; checkpoints under <code>refs/gatos/state/**</code>.</td></tr><tr><td><strong>Why GATOS</strong></td><td>Any node can replay to the same byte-identical result; offline-first.</td></tr></tbody></table><hr><h2 id="_3-distributed-general-computer-agents-on-a-bus" tabindex="-1">3) Distributed General Computer (Agents on a Bus) <a class="header-anchor" href="#_3-distributed-general-computer-agents-on-a-bus" aria-label="Permalink to &quot;3) Distributed General Computer (Agents on a Bus)&quot;">​</a></h2><p><a id="3"></a><a id="3-distributed-general-computer-agents-on-a-bus"></a></p><p><a id="3"></a><a id="3-distributed-general-computer-agents-on-a-bus"></a></p><table tabindex="0"><thead><tr><th></th><th></th></tr></thead><tbody><tr><td><strong>Goal</strong></td><td>Multi-agent orchestration with exactly-once semantics and audit.</td></tr><tr><td><strong>How</strong></td><td>Git message bus (<code>refs/gatos/mbus/**</code>) with acks/commitments; capabilities gate topics.</td></tr><tr><td><strong>Why GATOS</strong></td><td>Works without Kafka; merges cleanly; persists forever.</td></tr></tbody></table><hr><h2 id="_4-supply-chain-deploy-attestation" tabindex="-1">4) Supply-Chain &amp; Deploy Attestation <a class="header-anchor" href="#_4-supply-chain-deploy-attestation" aria-label="Permalink to &quot;4) Supply-Chain &amp; Deploy Attestation&quot;">​</a></h2><p><a id="4"></a><a id="4-supply-chain--deploy-attestation"></a></p><p><a id="4"></a><a id="4-supply-chain--deploy-attestation"></a><a id="4-supply-chain-deploy-attestation"></a></p><table tabindex="0"><thead><tr><th></th><th></th></tr></thead><tbody><tr><td><strong>Goal</strong></td><td>Immutable, signed, verifiable deploy records.</td></tr><tr><td><strong>How</strong></td><td>Ship every critical action as an event; store stdout/stderr as notes; multi-sig trust for policy changes.</td></tr><tr><td><strong>Why GATOS</strong></td><td>Incident response + compliance with zero vendor lock.</td></tr></tbody></table><hr><h2 id="_5-air-gapped-ml-registry" tabindex="-1">5) Air-Gapped ML Registry <a class="header-anchor" href="#_5-air-gapped-ml-registry" aria-label="Permalink to &quot;5) Air-Gapped ML Registry&quot;">​</a></h2><p><a id="5"></a><a id="5-air-gapped-ml-registry"></a></p><p><a id="5"></a><a id="5-air-gapped-ml-registry"></a></p><table tabindex="0"><thead><tr><th></th><th></th></tr></thead><tbody><tr><td><strong>Goal</strong></td><td>Version large models/datasets with provenance and selective export.</td></tr><tr><td><strong>How</strong></td><td>Opaque pointers for ciphertext artifacts; policies for export; epochs bound repo growth.</td></tr><tr><td><strong>Why GATOS</strong></td><td>Portable archives; verifiable lineage; offline friendly.</td></tr></tbody></table><hr><h2 id="_6-cross-app-data-sharing-rls-gated" tabindex="-1">6) Cross-App Data Sharing (RLS-gated) <a class="header-anchor" href="#_6-cross-app-data-sharing-rls-gated" aria-label="Permalink to &quot;6) Cross-App Data Sharing (RLS-gated)&quot;">​</a></h2><p><a id="6"></a><a id="6-cross-app-data-sharing-rls-gated"></a></p><p><a id="6"></a><a id="6-cross-app-data-sharing-rls-gated"></a></p><table tabindex="0"><thead><tr><th></th><th></th></tr></thead><tbody><tr><td><strong>Goal</strong></td><td>App A reads materialized state from App B without custom APIs.</td></tr><tr><td><strong>How</strong></td><td>App B publishes state under <code>refs/gatos/state/&lt;ns&gt;</code>; App A fetches and enforces RLS via shared policy bundles.</td></tr><tr><td><strong>Why GATOS</strong></td><td>Zero glue code; shared Merkle truth.</td></tr></tbody></table><hr><h2 id="_7-knowledge-graph-for-code-ops" tabindex="-1">7) Knowledge Graph for Code &amp; Ops <a class="header-anchor" href="#_7-knowledge-graph-for-code-ops" aria-label="Permalink to &quot;7) Knowledge Graph for Code &amp; Ops&quot;">​</a></h2><p><a id="7"></a><a id="7-knowledge-graph-for-code--ops"></a></p><p><a id="7"></a><a id="7-knowledge-graph-for-code--ops"></a><a id="7-knowledge-graph-for-code-ops"></a></p><table tabindex="0"><thead><tr><th></th><th></th></tr></thead><tbody><tr><td><strong>Goal</strong></td><td>Persist “why” relationships alongside “what” code changes.</td></tr><tr><td><strong>How</strong></td><td>Edges as journal events; roaring-bitmap caches for fast queries.</td></tr><tr><td><strong>Why GATOS</strong></td><td>Time-travelable semantics baked into Git.</td></tr></tbody></table><hr><h2 id="_8-regulated-feature-flags-config" tabindex="-1">8) Regulated Feature Flags &amp; Config <a class="header-anchor" href="#_8-regulated-feature-flags-config" aria-label="Permalink to &quot;8) Regulated Feature Flags &amp; Config&quot;">​</a></h2><p><a id="8"></a><a id="8-regulated-feature-flags--config"></a></p><p><a id="8"></a><a id="8-regulated-feature-flags--config"></a><a id="8-regulated-feature-flags-config"></a></p><table tabindex="0"><thead><tr><th></th><th></th></tr></thead><tbody><tr><td><strong>Goal</strong></td><td>Signed toggles with audit and rollbacks.</td></tr><tr><td><strong>How</strong></td><td>KV-style events + index refs; push-gate for enforcement.</td></tr><tr><td><strong>Why GATOS</strong></td><td>Auditable configuration without a new database.</td></tr></tbody></table>',47)])])}const u=a(o,[["render",s]]);export{c as __pageData,u as default};
